  var uri = new Uri(inviteUrl._getLocation());
  var sessIdPart = ANCHOR_NAME + '=' + sessionId;

  var anchor;
  if (uri.anchor()) {
    anchor = uri.anchor() + '&' + sessIdPart;
  } else {
    anchor = sessIdPart;
  }

  uri.setAnchor(anchor);

  return uri.toString();
